// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "openzeppelin-solidity/contracts/utils/cryptography/draft-EIP712.sol";
import "openzeppelin-solidity/contracts/utils/cryptography/ECDSA.sol";

/**
 * @dev Verification of MetaTXs for Payments using EIP712.
 *
 * This contract just defines the structure of a Payment Input
 * and exposes a verify function, using the EIP712 code by OpenZeppelin
 */

contract EIP712Verifier is EIP712 {
    using ECDSA for bytes32;
    bytes32 private constant _TYPEHASH =
        keccak256(
            "PaymentInput(bytes32 paymentId,uint256 amount,uint16 feeBPS,uint256 universeId,uint256 validUntil,address buyer,address seller)"
        );

    struct PaymentInput {
        bytes32 paymentId;
        uint256 amount;
        uint16 feeBPS;
        uint256 universeId;
        uint256 validUntil;
        address buyer;
        address seller;
    }

    constructor() EIP712("EIP712-FV-Payments", "0.0.1") {}

    function verify(
        PaymentInput calldata inp,
        bytes calldata signature,
        address signer
    ) public view returns (bool) {
        address recoveredSigner = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    _TYPEHASH,
                    inp.paymentId,
                    inp.amount,
                    inp.feeBPS,
                    inp.universeId,
                    inp.validUntil,
                    inp.buyer,
                    inp.seller
                )
            )
        ).recover(signature);
        return signer == recoveredSigner;
    }
}

